<script>
export default {
  data() {
    return {
      particles: Array.from({ length: 20 }, (_, i) => ({
      id: i,
      size: Math.random() * 15 + 5,
      x: Math.random() * 100,
      y: Math.random() * 100,
      delay: Math.random() * 20,
      duration: 15 + Math.random() * 30,
      blur: Math.random() * 2,
    }))
    };
  },
  mounted() {
    // Agregar efecto de movimiento aleatorio al pasar el mouse
    const icons = document.querySelectorAll('.language-icon');
    
    icons.forEach(icon => {
      icon.addEventListener('mousemove', (e) => {
        const x = e.clientX - icon.getBoundingClientRect().left;
        const y = e.clientY - icon.getBoundingClientRect().top;
        
        const centerX = icon.offsetWidth / 2;
        const centerY = icon.offsetHeight / 2;
        
        const moveX = (x - centerX) / 10;
        const moveY = (y - centerY) / 10;
        
        icon.style.transform = `translate(${moveX}px, ${moveY}px)`;
      });
      
      icon.addEventListener('mouseleave', () => {
        icon.style.transform = 'translate(0, 0)';
      });
    });
  },
  methods: {
      particleStyle(particle) {
        return {
          width: `${particle.size}px`,
          height: `${particle.size}px`,
          left: `${particle.x}%`,
          top: `${particle.y}%`,
          animationDelay: `${particle.delay}s`,
          animationDuration: `${particle.duration}s`,
          filter: `blur(${particle.blur}px)`,
        };
      },
    downloadCV() {
      const fileUrl = '/CV_Eliazar_Rebollo.pdf';

      const link = document.createElement('a');
      link.href = fileUrl;
      link.download = 'CV_Eliazar_Rebollo.pdf';
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    },
  },
}
</script>